import React from 'react'
import {ContactContainer,ContactH1,Button} from './ContactElements';
import {Form,FormGroup,Input,Col} from 'reactstrap';
import * as emailjs from 'emailjs-com';

const Contact = () => {

    function sendEmail(e) {
        e.preventDefault();
    
        emailjs.sendForm('service_scalbhk', 'service_scalbhk', e.target, 'user_PQnpvNT0dJAwBLWXv0yO4')
          .then((result) => {
              console.log(result.text);
          }, (error) => {
              console.log(error.text);
          });
      }

    return (
            <ContactContainer>
            <ContactH1>Contact</ContactH1>
            <Col sm={{size:4, offset:4}}>
            <Form onSubmit={sendEmail}>
                <FormGroup>
                    <Input type="text" name="from_name" placeholder="NAME" ></Input>
                </FormGroup>
                <FormGroup>
                <Input type="name" name="from_email" placeholder="EMAILL" ></Input>
                </FormGroup>
                <FormGroup>
                <Input type="textarea" name="message" placeholder="MESSAGE"></Input>
                </FormGroup>
                <Button type="submit" value="Send">Submit</Button>
            </Form>

            <form className="contact-form" onSubmit={sendEmail}>
            <input type="text" name="from_name" />
            <label>Email</label>
            <input type="email" name="user_email" />
            <label>Message</label>
            <textarea name="message" />
            <input type="submit" value="Send" />
          </form>
          </Col>
          </ContactContainer>
    );
}

export default Contact;
