import React from 'react'
import {ContactContainer,ContactH1,Input} from './ContactElements';
import emailjs from 'emailjs-com';

const Contact = () => {

    function sendEmail(e) {
        e.preventDefault();
    
        emailjs.sendForm('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', e.target, 'YOUR_USER_ID')
          .then((result) => {
              console.log(result.text);
          }, (error) => {
              console.log(error.text);
          });
      }

    return (
            <ContactContainer>
            <ContactH1>Contact</ContactH1>
            <Col sm={{size:4, offset:4}}>
            <Form>
                <FormGroup>
                    <Input type="name" name="name" id="name" placeholder="NAME" ></Input>
                </FormGroup>
                <FormGroup>
                <Input type="name" name="name" id="name" placeholder="NAME" ></Input>
                </FormGroup>
                <FormGroup>
                <Input type="textarea" name="text" id="message" placeholder="MESSAGE"></Input>
                </FormGroup>
            </Form>
            <form className="contact-form" onSubmit={sendEmail}>
            <input type="text" name="contact_number" placeholde="NAME" />
            
            <input type="text" name="user_name" />
            <label>Email</label>
            <input type="email" name="user_email" />
            <label>Message</label>
            <textarea name="message" />
            <input type="submit" value="Send" />
          </form>
          </Col>
          </ContactContainer>
    );
}

export default Contact;
